SELECT tsc.id, CASE WHEN tsc.contract_type = "PXCJ" THEN ti.investCode WHEN tsc.contract_type = "PX_JKXY" THEN "PXJK" WHEN tsc.contract_type = "FYZHXY" THEN "FYZHXY" WHEN tsc.contract_type = "PTZCXY" THEN "PTZCXY" WHEN tsc.contract_type = "CJJHXY" THEN "CJJHXY" ELSE "" END AS contractSeal , tb.borrowAliasNo AS borrowAliasNo, tb.borrowAlias AS borrowAlias, tb.loanNumber AS loanNumber, tuj.userName AS userName, tu.userName AS cuserName, CASE WHEN tsc.contract_type = "PXCJ" THEN ti.investAmount WHEN tsc.contract_type = "PXJK" THEN tb.BORROWAMOUNT WHEN tsc.contract_type = "FYZHXY" THEN "FYZHXY" WHEN tsc.contract_type = "PTZCXY" THEN "PTZCXY" WHEN tsc.contract_type = "CJJHXY" THEN "CJJHXY" ELSE "" END AS investAmount , tb.ANUALRATE AS ANUALRATE, CASE WHEN tsc.contract_type = "PXCJ" THEN tb.DEADLINE WHEN tsc.contract_type = "PXJK" THEN tb.DEADLINE WHEN tsc.contract_type = "FYZHXY" THEN "FYZHXY" WHEN tsc.contract_type = "PTZCXY" THEN "PTZCXY" WHEN tsc.contract_type = "CJJHXY" THEN "CJJHXY" ELSE "" END AS DEADLINE , CASE WHEN tsc.contract_type = "PXCJ" THEN ti.interestAccrualDate WHEN tsc.contract_type = "PXJK" THEN tb.contractSigningTime WHEN tsc.contract_type = "FYZHXY" THEN now() WHEN tsc.contract_type = "PTZCXY" THEN now() WHEN tsc.contract_type = "CJJHXY" THEN now() ELSE NOW() END AS interestAccrualDate , tsc.contract_type AS contractType FROM
t_contract_seal tsc LEFT JOIN t_invest ti ON tsc.user_id = ti.investor LEFT JOIN t_user tu ON ti.investor = tu.id LEFT JOIN t_borrow tb ON ti.borrowId = tb.BORROW_ID INNER JOIN t_user tuj ON tb.USERID = tuj.id
t_contract_seal tsc LEFT JOIN t_invest ti ON tsc.user_id = ti.investor LEFT JOIN t_user tu ON ti.investor = tu.id LEFT JOIN t_borrow tb ON ti.borrowId = tb.BORROW_ID INNER JOIN t_user tuj ON tb.USERID = tuj.id
AND contract_type = #{contractType}
AND ti.interestAccrualDate BETWEEN #{beginCreateTime} AND #{endCreateTime}
AND tuj.userName = #{borrowName}
AND tb.borrowAliasNo = #{borrowAliasNo}
AND tb.borrowAlias = #{borrowAlias}
AND tu.userName = #{investName}
AND ti.investCode = #{contractSeal}
ORDER BY interestAccrualDate desc

choose.when:
