SELECT tcs.id as contractSeal, tb.borrowAliasNo as borrowAliasNo, tb.borrowAlias as borrowAlias, tb.loanNumber as loanNumber, tuj.userName as userName, tu.userName as cuserName, tb.BORROWAMOUNT as investAmount, tb.ANUALRATE as ANUALRATE, tb.DEADLINE as DEADLINE, tb.contractSigningTime as interestAccrualDate, tcs.contract_type AS contractType from t_borrow tb LEFT JOIN t_contract_seal tcs ON tb.USERID = tcs.user_id LEFT JOIN t_user tuj ON tb.USERID = tuj.id LEFT JOIN t_invest ti ON tb.BORROW_ID = ti.borrowId LEFT JOIN t_user tu ON tu.id = ti.investor where tcs.contract_type = 'PX_JKXY'
AND contract_type = #{contractType}
AND tb.contractSigningTime BETWEEN #{beginCreateTime} AND #{endCreateTime}
AND tuj.userName = #{borrowName}
AND tb.borrowAliasNo = #{borrowAliasNo}
AND tb.borrowAlias = #{borrowAlias}
AND tu.userName = #{investName}
AND ti.investCode = #{contractSeal}
ORDER BY interestAccrualDate desc

choose.otherwise:
SELECT tcs.id as contractSeal, tb.borrowAliasNo as borrowAliasNo, tb.borrowAlias as borrowAlias, tb.loanNumber as loanNumber, tuj.userName as userName, tu.userName as cuserName, ti.investAmount as investAmount, tb.ANUALRATE as ANUALRATE, tb.DEADLINE as DEADLINE, ti.interestAccrualDate as interestAccrualDate, tcs.contract_type AS contractType FROM t_contract_seal tcs LEFT JOIN t_invest ti ON tcs.user_id = ti.investor LEFT JOIN t_user tu ON tu.id = ti.investor LEFT JOIN t_borrow tb ON tb.BORROW_ID = ti.borrowId LEFT JOIN t_user tuj ON tuj.id = tb.USERID where tcs.contract_type = "PX_CJXY"
AND contract_type = #{contractType}
AND ti.interestAccrualDate BETWEEN #{beginCreateTime} AND #{endCreateTime}
AND tuj.userName = #{borrowName}
AND tb.borrowAliasNo = #{borrowAliasNo}
AND tb.borrowAlias = #{borrowAlias}
AND tu.userName = #{investName}
AND ti.investCode = #{contractSeal}
union all SELECT tcs.id as contractSeal, tb.borrowAliasNo as borrowAliasNo, tb.borrowAlias as borrowAlias, tb.loanNumber as loanNumber, tuj.userName as userName, tu.userName as cuserName, tb.BORROWAMOUNT as investAmount, tb.ANUALRATE as ANUALRATE, tb.DEADLINE as DEADLINE, tb.contractSigningTime as interestAccrualDate, tcs.contract_type AS contractType from t_borrow tb LEFT JOIN t_contract_seal tcs ON tb.USERID = tcs.user_id LEFT JOIN t_user tuj ON tb.USERID = tuj.id LEFT JOIN t_invest ti ON tb.BORROW_ID = ti.borrowId LEFT JOIN t_user tu ON tu.id = ti.investor where tcs.contract_type = 'PX_JKXY'
AND contract_type = #{contractType}
AND tb.contractSigningTime BETWEEN #{beginCreateTime} AND #{endCreateTime}
AND tuj.userName = #{borrowName}
AND tb.borrowAliasNo = #{borrowAliasNo}
AND tb.borrowAlias = #{borrowAlias}
AND tu.userName = #{investName}
AND ti.investCode = #{contractSeal}
ORDER BY interestAccrualDate desc

mapper.select:
SELECT tsc.id, tsc.contract_id AS contractSeal, tb.borrowAliasNo AS borrowAliasNo, tb.borrowAlias AS borrowAlias, tb.loanNumber AS loanNumber, tu.userName AS userName, '---' AS cuserName, tb.BORROWAMOUNT AS investAmount, tb.ANUALRATE AS ANUALRATE, tb.DEADLINE AS DEADLINE, tb.full_borrow_date AS interestAccrualDate, tsc.contract_type AS contractType
FROM t_contract_seal tsc LEFT JOIN t_invest ti ON tsc.reference_id = ti.id LEFT JOIN t_borrow tb ON tb.borrow_id = tsc.reference_id LEFT JOIN t_user tu ON tu.id = tb.userid
AND contract_type = #{contractType}
AND contract_type in('PX_JKXY','XYZXXY')
AND ti.interestAccrualDate BETWEEN #{beginCreateTime} AND #{endCreateTime}
AND tu.userName = #{borrowName}
AND tb.borrowAliasNo = #{borrowAliasNo}
AND tb.borrowAlias = #{borrowAlias}
AND tu.userName = #{investName}
AND tsc.contract_id = #{contractSeal}
and tsc.status='3' and tsc.contract_type in('PX_JKXY','XYZXXY')
UNION ALL SELECT tsc.id, tsc.contract_id AS contractSeal, tb.borrowAliasNo AS borrowAliasNo, tb.borrowAlias AS borrowAlias, tb.loanNumber AS loanNumber, tu.userName AS userName, '---' AS cuserName, tb.BORROWAMOUNT AS investAmount, tb.ANUALRATE AS ANUALRATE, tb.DEADLINE AS DEADLINE, tb.full_borrow_date AS interestAccrualDate, tsc.contract_type AS contractType
FROM t_contract_seal tsc LEFT JOIN t_invest ti ON tsc.reference_id = ti.id LEFT JOIN t_borrow tb ON tb.loanNumber = tsc.reference_id LEFT JOIN t_user tu ON tu.id = tb.userid
AND contract_type = #{contractType}
AND contract_type in('HKTXH')
AND ti.interestAccrualDate BETWEEN #{beginCreateTime} AND #{endCreateTime}
AND tu.userName = #{borrowName}
AND tb.borrowAliasNo = #{borrowAliasNo}
AND tb.borrowAlias = #{borrowAlias}
AND tu.userName = #{investName}
AND tsc.contract_id = #{contractSeal}
and tsc.status='3' and tsc.contract_type in('HKTXH')
UNION ALL SELECT tsc.id, tsc.contract_id AS contractSeal, tb.borrowAliasNo AS borrowAliasNo, tb.borrowAlias AS borrowAlias, tb.loanNumber AS loanNumber, '---' AS userName, tu.userName AS cuserName, ti.investAmount AS investAmount, tb.ANUALRATE AS ANUALRATE, tb.DEADLINE AS DEADLINE, tb.full_borrow_date AS interestAccrualDate, tsc.contract_type AS contractType
FROM t_contract_seal tsc LEFT JOIN t_invest ti ON tsc.reference_id = ti.id LEFT JOIN t_borrow tb ON tb.borrow_id = ti.borrowId LEFT JOIN t_user tu ON tu.id = tb.userid
AND contract_type = #{contractType}
AND contract_type in('PX_CJZXXY')
AND ti.interestAccrualDate BETWEEN #{beginCreateTime} AND #{endCreateTime}
AND tu.userName = #{borrowName}
AND tb.borrowAliasNo = #{borrowAliasNo}
AND tb.borrowAlias = #{borrowAlias}
AND tu.userName = #{investName}
AND tsc.contract_id = #{contractSeal}
and tsc.status='3' and tsc.contract_type in('PX_CJZXXY')
ORDER BY interestAccrualDate desc,contractSeal,contractType
