mapper.select:
SELECT t1.id, t4.id AS "tinvestId", t1.asset_code AS "assetCode", t2.borrowtitle, t5.realname AS "investRealname" , t4.investamount, t3.realname AS "borrowRealname", t4.capital_rate AS "capitalRate", t2.deadline, t2.surplus_deadlline AS "surplusDeadlline", t2.pay_method AS "payMethod", t2.lendingtime, t2.borrowstatus, t2.create_time as "createTime", t2.loannumber,amount FROM t_asset t1 LEFT JOIN t_borrow t2 ON t1.borrow_id=t2.borrow_id LEFT JOIN t_user t3 ON t2.userid=t3.id LEFT JOIN t_invest t4 ON t4.borrowid=t2.borrow_id LEFT JOIN t_user t5 ON t5.id=t4.investor
and t2.pay_method =#{payMethod}
and t2.borrowstatus =#{borrowstatus}
AND t2.create_time >= #{createTimeBeginTimes}
AND t2.create_time <= #{createTimeEndTimes}
AND t2.lendingtime >= #{lendingtimeBeginTimes}
AND t2.lendingtime <= #{lendingtimeEndTimes}
and t3.realname like concat('%',#{borrowRealname},'%')
and t1.asset_code like concat('%',#{assetCode},'%')
and t2.loannumber like concat('%',#{loannumber},'%')
and t1.asset_type='1'
ORDER BY assetCode DESC

SELECT 

FROM t_borrow_repay_billplan a
WHERE a.investId = #{investid}
