mapper.select:
SELECT 

FROM t_finance_bespeak a

WHERE a.id = #{id}

		SELECT 

		FROM t_finance_bespeak a




				AND a.name = #{name}



				AND a.status = #{status}



                AND a.create_time >= #{beginCreateTime}


                AND a.create_time <= #{endCreateTime}


         		AND a.status != '6' 




		ORDER BY a.create_time DESC



				ORDER BY ${page.orderBy}

SELECT 

FROM t_finance_bespeak a






		ORDER BY ${page.orderBy}
