mapper.select:
SELECT 
	
FROM t_finance a

WHERE a.id = #{id}

		SELECT 
			,ROUND(a.already_collected_amount*100/a.collect_amount,2) as scale
		FROM t_finance a
		
		
			
			
				AND a.name = #{name}
			
			
			
				AND a.finance_code = #{financeCode}
			
			
			
			
			
				   
					AND a.status in ('4','5')
				
				
					AND a.status in ('6','7','8')
				
				
					AND a.status = #{status}
				
			
			
			
			
			
                AND a.create_time >= #{beginCreateTime}
            
            
                AND a.create_time <= #{endCreateTime}
            
				AND a.status != '10'
		
				ORDER BY a.create_time DESC

		SELECT 
		
		COUNT(a.STATUS) AS statusCount ,a.STATUS AS status
		
		FROM t_finance a 
		
		
			
			
				AND a.name = #{name}
			
			
			
				AND a.finance_code = #{financeCode}
			
			
			
			
			
				   
					AND a.status in ('4','5')
				
				
					AND a.status in ('6','7','8')
				
				
					AND a.status = #{status}
				
			
			
			
			
			
                AND a.create_time >= #{beginCreateTime}
            
            
                AND a.create_time <= #{endCreateTime}
            
	
		AND a.status != '10'
		
	
		
		GROUP BY  a.status

		SELECT 
		
		SUM(a.collect_amount)
		
		FROM t_finance a
		
		
			
			
				AND a.name = #{name}
			
			
			
				AND a.finance_code = #{financeCode}
			
			
			
			
			
				   
					AND a.status in ('4','5')
				
				
					AND a.status in ('6','7','8')
				
				
					AND a.status = #{status}
				
			
			
			
			
			
                AND a.create_time >= #{beginCreateTime}
            
            
                AND a.create_time <= #{endCreateTime}
            
				AND a.status != '10'

SELECT COUNT(*) FROM t_finance a
AND a.status in('1','2','3','4','5')
AND a.product_id=#{productId}
