mapper.sql:
		tc.capital_category = '1'
		AND tc.capital_type = '1'

			AND tf.product_id = #{financeProductId}


			AND tf.finance_code like CONCAT('%',TRIM(#{financeCode}),'%')


			AND 


					 tc.`status` IN ('2','3','5') AND DATE_FORMAT(NOW(), '%Y-%m-%d') < tc.back_end_date 


					 tc.`status` IN ('2','3','5') AND DATE_FORMAT(NOW(), '%Y-%m-%d') >= tc.back_end_date 


					tc.`status` = #{capitalStatus}




                AND tc.invest_time >= #{capitalInvestTimeBegin} 


                AND tc.invest_time <= #{capitalInvestTimeEnd} 


			AND tu.mobile like CONCAT('%',TRIM(#{mobile}),'%')


			AND tu.realName like CONCAT('%',TRIM(#{userName}),'%')


			AND tc.capital_code like CONCAT('%',TRIM(#{capitalCode}),'%')

mapper.select:
SELECT
FROM t_capital tc JOIN t_finance tf ON tf.id = tc.finance_id JOIN t_user tu ON tu.id = tc.user_id
ORDER BY ${page.orderBy}

SELECT 

FROM t_capital a






		ORDER BY ${page.orderBy}

SELECT * FROM (

WHERE tc.id = #{capitalId} 
ORDER BY investTime
) tmp
UNION

WHERE tc.source_capital_id = #{capitalId} 
ORDER BY investTime
