mapper.select:
SELECT SUM(a.servicecharge) AS servicechargecount,SUM(a.risk_reserve) AS riskReservecount,0.00 AS outriskReservecount FROM t_borrow a left join t_user tu on a.USERID = tu.id
a.BORROWSTATUS IN (11,12) AND a.borrowAliasNo IS NOT NULL
AND a.borrowaliasno like "%${borrowaliasno}%"
AND a.borrowalias like "%${borrowalias}%"
AND a.loannumber like "%${loannumber}%"
AND tu.realName LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')
AND a.servicecharge >= #{lowservicecharge}
AND a.servicecharge <= #{highservicecharge}
AND a.lendingtime >= #{startlendingtime}
AND a.lendingtime <= #{endlendingtime}

SELECT ,tu.realName as userName,tb.audit_suggest as bzInfo,CONCAT(tiv.hasDeadline,"/",tiv.deadline) AS ratio, CASE 1 WHEN UNIX_TIMESTAMP(a.openborrowdate)>UNIX_TIMESTAMP(NOW()) THEN '21' ELSE a.BORROWSTATUS END AS borrowstatus, CONCAT(SUBSTR(a.idCardNo,1,3),'***********',SUBSTR(a.idCardNo,-4)) AS idcardno, CASE a.pay_method WHEN 'debx' THEN '等额本息' WHEN 'xxhb' THEN '先息后本' WHEN 'ychbx' THEN '一次性还本付息' ELSE '' END AS "payMethod" FROM t_borrow a left join t_user tu on a.USERID = tu.id LEFT JOIN t_borrow_audit_record tb ON a.BORROW_ID = tb.audit_bid LEFT JOIN t_invest tiv ON a.BORROW_ID=tiv.borrowId
a.BORROWSTATUS IN (11,12) AND a.borrowAliasNo IS NOT NULL
AND a.borrowaliasno like "%${borrowaliasno}%"
AND a.borrowalias like "%${borrowalias}%"
AND a.loannumber like "%${loannumber}%"
AND tu.realName LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')
AND a.servicecharge >= #{lowservicecharge}
AND a.servicecharge <= #{highservicecharge}
AND a.lendingtime >= #{startlendingtime}
AND a.lendingtime <= #{endlendingtime}
GROUP BY a.loanNumber
ORDER BY ${page.orderBy}
ORDER BY a.lendingTime DESC

SELECT SUM(tbb.advances_amount) AS advancesCount,SUM(tbb.offset_amount) AS offsetCount FROM t_borrow_billplan tbb LEFT JOIN t_borrow tbr ON tbb.apply_id = tbr.loanNumber LEFT JOIN t_user tu ON tbr.USERID=tu.id
AND tbb.is_overdue='1' AND (tbb.is_advances='4' OR tbb.is_offset='2')
AND tbr.borrowAliasNo like "%${borrowaliasno}%"
AND tbb.apply_id like "%${applyId}%"
AND tbb.advances_time > #{startAdvancesTime}
AND tbb.advances_time < #{endAdvancesTime}
AND tbb.offset_time > #{startOffsetTime}
AND tbb.offset_time < #{endOffsetTime}
AND tu.realName LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')

SELECT tbr.BORROW_ID AS borrowId,tbb.period,tbr.borrowAliasNo AS borrowaliasno,tu.realName AS userName,tbr.USERID AS userid,tbb.repay_time AS repayTime, CONCAT(SUBSTR(tbr.idCardNo,1,3),'***********',SUBSTR(tbr.idCardNo,-4)) AS idcardno,tbr.BORROWAMOUNT AS borrowamount,tbb.advances_amount AS advancesSum,tbb.offset_amount AS offsetSum, tbr.mobile,tbr.borrowCode AS borrowcode,tbb.month_capital,tbb.month_interest,tbb.apply_id AS applyId,tbb.is_advances AS isAdvances, tbb.manage_fee AS manageFee,tbb.fails_charge_origin AS failsChargeOrigin,tbb.late_charge_origin AS lateChargeOrigin,tbb.monthly_payment_origin AS monthlyPaymentOrigin, tbb.repay_status AS repayStatus,tbb.is_offset AS isOffset,tbb.advances_time AS advancesTime,tbb.offset_time AS offsetTime,tbb.overdue_day AS overdueDay FROM t_borrow_billplan tbb LEFT JOIN t_borrow tbr ON tbb.apply_id = tbr.loanNumber LEFT JOIN t_user tu ON tbr.USERID=tu.id
AND tbb.is_overdue='1' AND tbb.is_advances ='4'
AND tbr.borrowAliasNo like "%${borrowaliasno}%"
AND tbb.apply_id like "%${applyId}%"
AND tbb.advances_time > #{startAdvancesTime}
AND tbb.advances_time < #{endAdvancesTime}
AND tbb.offset_time > #{startOffsetTime}
AND tbb.offset_time < #{endOffsetTime}
AND tu.realName LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')
ORDER BY ${page.orderBy}

SELECT ,tu.realName as userName,tb.audit_suggest as bzInfo,CONCAT(tiv.hasDeadline,"/",tiv.deadline) AS ratio, CASE 1 WHEN UNIX_TIMESTAMP(a.openborrowdate)>UNIX_TIMESTAMP(NOW()) THEN '21' ELSE a.BORROWSTATUS END AS borrowstatus, CONCAT(SUBSTR(a.idCardNo,1,3),'***********',SUBSTR(a.idCardNo,-4)) AS idcardno, CASE a.pay_method WHEN 'debx' THEN '等额本息' WHEN 'xxhb' THEN '先息后本' WHEN 'ychbx' THEN '一次性还本付息' ELSE '' END AS "payMethod" FROM t_borrow a left join t_user tu on a.USERID = tu.id LEFT JOIN t_borrow_audit_record tb ON a.BORROW_ID = tb.audit_bid LEFT JOIN t_invest tiv ON a.BORROW_ID=tiv.borrowId
a.BORROWSTATUS IN (11,12) AND a.borrowAliasNo IS NOT NULL
AND a.borrowaliasno like "%${borrowaliasno}%"
AND a.borrowalias like "%${borrowalias}%"
AND a.loannumber like "%${loannumber}%"
AND tu.realName LIKE CONCAT('%',#{userName},'%')
AND a.servicecharge >= #{lowservicecharge}
AND a.servicecharge <= #{highservicecharge}
AND a.lendingtime >= #{startlendingtime}
AND a.lendingtime <= #{endlendingtime}
GROUP BY a.loanNumber
ORDER BY ${page.orderBy}
ORDER BY a.lendingTime DESC
LIMIT 20000
