mapper.select:
select t1.id, asset_code, realname, amount, residue_amount, raise_term, nper, rate, entry_time, product_id as "productId", original_asset_id as "originalAssetId" , t1.status, custom_weight as "customWeight", weight, stick_top as "stickTop", source_asset_id as "sourceAssetId", matching_pen_number as "matchingPenNumber", idcard, t4.borrowCode as "borrowcode", t1.asset_category as "assetCategory" from t_asset t1 left join t_user t2 on t2.id=t1.user_id left join t_user_detail t3 on t3.userid=t1.user_id left join t_borrow t4 on t4.borrow_id=t1.borrow_id
and t4.borrowCode =#{borrowcode}
and asset_code =#{assetCode}
and realName like concat('%',#{realName},'%')
and idCard =#{idCard}
and product_id =#{productId}
and original_asset_id =#{originalAssetId}
and status=#{status}
and source_asset_id=#{sourceAssetId}
and t1.asset_category=#{assetCategory}
and entry_time between #{beginTimes} and #{endTimes}
and weight between #{beginWeight} and #{endWeight}
and stick_top=#{stickTop}
ORDER BY weight DESC

mapper.update:
update t_asset SET 	

		stick_top =#{isStickTop},


		custom_weight = #{addWeight},


		weight = #{cus}

	WHERE id = #{id}
