mapper.select:
SELECT 

FROM t_ircoupon a

WHERE a.id = #{id}

SELECT 
	,a.startdate AS startdate,
	CASE a.profitTime WHEN -1 THEN '永久' else a.profitTime END as profittime,

	CASE a.status WHEN '1' THEN '待发布' WHEN '2' THEN '进行中' WHEN '3' THEN '已停止' WHEN '4' THEN '已结束' END as status 
FROM t_ircoupon a



		AND a.id = #{id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.startdate >= #{beginStartdate}


		AND a.enddate <=  #{endStartdate}


		AND a.usetype = #{usetype}


		AND a.status = #{status}



		AND a.profittime ='-1'


			AND a.profittime !='-1'




		AND a.startdate IS NULL AND a.enddate IS NULL


			AND a.startdate IS NOT NULL AND a.enddate IS NOT NULL



		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.status,create_date DESC

SELECT , CASE a.profitTime WHEN -1 THEN '永久' else a.profitTime END as profittime, a.startdate as startdate, CASE a.status WHEN '1' THEN '待发布' WHEN '2' THEN '进行中' WHEN '3' THEN '已停止' WHEN '4' THEN '已结束' END as status FROM t_ircoupon a
AND a.id = #{id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.startdate >= #{beginStartdate}
AND a.enddate <= #{endStartdate}
AND a.usetype = #{usetype}
AND a.status = #{status}
AND a.profittime ='-1'
AND a.profittime !='-1'
AND a.startdate IS NULL AND a.enddate IS NULL
AND a.startdate IS NOT NULL AND a.enddate IS NOT NULL
AND a.create_by = #{createBy.id}
ORDER BY a.status,create_date DESC

SELECT 
	SUM(a.sendNumber) as sendNumberCount ,
	SUM(a.usedNumber) as usedNumberCount ,
	SUM(a.expireNumber) as expireNumberCount ,
	SUM(a.profitAmount) as profitAmountCount
FROM t_ircoupon a


		AND a.id = #{id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.startdate >= #{beginStartdate}


		AND a.enddate <=  #{endStartdate}


		AND a.usetype = #{usetype}


		AND a.status = #{status}



			AND a.profittime ='-1'


			AND a.profittime !='-1'




		AND a.startdate IS NULL AND a.enddate IS NULL


			AND a.startdate IS NOT NULL AND a.enddate IS NOT NULL



		AND a.create_by = #{createBy.id}

SELECT 

FROM t_ircoupon a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_ircoupon SET 	
	status = #{status}
WHERE id = #{id}

UPDATE t_ircoupon SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
