mapper.select:
SELECT

FROM t_ircoupon_detail a

WHERE a.id = #{id}

SELECT , CONCAT(SUBSTRING(a.realName,1,1),'**') as realName, CONCAT(SUBSTRING(a.mobile,1,3),'****',SUBSTRING(a.mobile,8)) as mobile, t.activityName AS activityName,i.name as ircouponName,i.scale as scale, CASE i.profitTime WHEN -1 THEN '永久' else i.profitTime END as profitTime, CASE a.uesdStatus WHEN '1' THEN '收益中' WHEN '2' THEN '未使用' WHEN '3' THEN '已使用' WHEN '4' THEN '已过期' END as uesdStatus, CASE a.sendStatus WHEN '1' THEN '已发' WHEN '2' THEN '待发' WHEN '3' THEN '失败' END as sendStatus, CASE a.investType WHEN '1' THEN '普享标' WHEN '2' THEN '出借计划' WHEN '3' THEN '其他' END as investType, CASE a.consumChannel WHEN '1' THEN 'web' WHEN '2' THEN '安卓' WHEN '3' THEN '苹果' WHEN '4' THEN '微信' WHEN '5' THEN '其他' END AS consumChannel, CASE a.sourceChannel WHEN '1' THEN 'web' WHEN '2' THEN '安卓' WHEN '3' THEN '苹果' WHEN '4' THEN '微信' WHEN '5' THEN '其他' END AS sourceChannel FROM t_ircoupon_detail a JOIN t_ircoupon i on a.ircouponId = i.id JOIN t_activity t on a.activityId = t.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.uesdStatus IN ('1','3')
AND a.realname = #{realname}
AND a.mobile = #{mobile}
AND i.profitTime ='-1'
AND i.profitTime !='-1'
AND i.name = #{ircouponName}
AND a.investtype = #{investtype}
AND a.uesdstatus = #{uesdstatus}
AND a.sendStatus = #{sendstatus}
AND a.consumchannel = #{consumchannel}
AND a.useddate BETWEEN #{beginUseddate} AND #{endUseddate}
AND t.activityName LIKE '%'||#{activityName}||'%' '%'+#{activityName}+'%' concat('%',#{activityName},'%')
AND a.senddate BETWEEN #{beginSenddate} AND #{endSenddate}
ORDER BY ${page.orderBy}
ORDER BY a.uesdStatus
ORDER BY a.sendStatus DESC,sendDate

SELECT , a.realName as realName, a.mobile as mobile, t.activityName AS activityName,i.name as ircouponName,i.scale as scale, CASE i.profitTime WHEN -1 THEN '永久' else i.profitTime END as profitTime, CASE a.uesdStatus WHEN '1' THEN '收益中' WHEN '2' THEN '未使用' WHEN '3' THEN '已使用' WHEN '4' THEN '已过期' END as uesdStatus, CASE a.sendStatus WHEN '1' THEN '已发' WHEN '2' THEN '待发' WHEN '3' THEN '失败' END as sendStatus, CASE a.investType WHEN '1' THEN '普享标' WHEN '2' THEN '出借计划' WHEN '3' THEN '其他' END as investType, CASE a.consumChannel WHEN '1' THEN 'web' WHEN '2' THEN '安卓' WHEN '3' THEN '苹果' WHEN '4' THEN '微信' WHEN '5' THEN '其他' END AS consumChannel, CASE a.sourceChannel WHEN '1' THEN 'web' WHEN '2' THEN '安卓' WHEN '3' THEN '苹果' WHEN '4' THEN '微信' WHEN '5' THEN '其他' END AS sourceChannel FROM t_ircoupon_detail a JOIN t_ircoupon i on a.ircouponId = i.id JOIN t_activity t on a.activityId = t.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.uesdStatus IN ('1','3')
AND a.realname = #{realname}
AND a.mobile = #{mobile}
AND i.profitTime ='-1'
AND i.profitTime !='-1'
AND i.name = #{ircouponName}
AND a.investtype = #{investtype}
AND a.uesdstatus = #{uesdstatus}
AND a.sendStatus = #{sendstatus}
AND a.consumchannel = #{consumchannel}
AND a.useddate BETWEEN #{beginUseddate} AND #{endUseddate}
AND t.activityName LIKE '%'||#{activityName}||'%' '%'+#{activityName}+'%' concat('%',#{activityName},'%')
AND a.senddate BETWEEN #{beginSenddate} AND #{endSenddate}
ORDER BY a.uesdStatus
ORDER BY a.sendStatus DESC,sendDate

SELECT
count(*) as counts,
count(distinct a.mobile) as userCounts,
SUM(REPLACE(a.investMoney,'-','')) as amounts
FROM t_ircoupon_detail a JOIN t_ircoupon i on a.ircouponId = i.id JOIN
t_activity t on a.activityId = t.id



		a.uesdStatus IN ('1','3')


		AND a.realname = #{realname}


		AND a.mobile = #{mobile}



			AND i.profitTime ='-1'


			AND i.profitTime !='-1'



		AND i.name = #{ircouponName}


		AND a.investtype = #{investtype}


		AND a.uesdstatus = #{uesdstatus}


		AND a.consumchannel = #{consumchannel}


		AND a.useddate BETWEEN #{beginUseddate} AND #{endUseddate}


		AND t.activityName LIKE
		'%'||#{activityName}||'%'
		'%'+#{activityName}+'%'
		concat('%',#{activityName},'%')


		AND a.senddate BETWEEN #{beginSenddate} AND #{endSenddate}

SELECT

FROM t_ircoupon_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_ircoupon_detail SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
