mapper.update:
update t_yx_rel_user
sum_a_invite_invest_amount=#{sumAInviteInvestAmount}, sum_b_invite_invest_amount=#{sumBInviteInvestAmount}, sum_invest_amount=#{sumInvestAmount}, sum_rebate_amount=#{sumRebateAmount},
WHERE user_mobile=#{userMobile}

mapper.select:
SELECT tu.id, t.id AS userId, t.userName AS userName, t.mobile AS userMobile, t.refferee, t.staffId, IF (t.staffId IS NULL, '1', '2') AS refferType, IFNULL(tu.sum_rebate_amount,0) as sumRebateAmount, IFNULL(tu.sum_a_invite_invest_amount,0) as sumAInviteInvestAmount, IFNULL(tu.sum_b_invite_invest_amount,0) as sumBInviteInvestAmount, IFNULL(tu.sum_complete_rebate_amount,0) as sumCompleteRebateAmount, IFNULL(tu.sum_invest_amount,0) as sumInvestAmount FROM t_user t LEFT JOIN t_yx_rel_user tu ON t.id = tu.user_id
and t.mobile=#{userMobile}
and t.staffId=#{staffId}

SELECT u.mobile as userMobile, u.realName as userName, u.staffId as staffId , ifnull(yu.sum_rebate_amount,0) as sumRebateAmount, ifnull(yu.sum_complete_rebate_amount,0) as sumCompleteRebateAmount, ifnull(yu.sum_a_invite_invest_amount,0) as sumAInviteInvestAmount, ifnull(yu.sum_b_invite_invest_amount,0) as sumBInviteInvestAmount, ifnull(yu.sum_invest_amount,0) as sumInvestAmount, ( SELECT count(1) FROM t_user u1 WHERE u1.refferee = u.mobile OR u1.refferee = u.staffId ) AS refferUserCount, ( SELECT count(1) FROM t_user u2 WHERE ( u2.refferee = u.mobile OR u2.refferee = u.staffId ) AND ( u2.isFuYou = '1' OR u2.isFuYouFlag = '1' ) ) AS refferIdcardCount, ( SELECT count(1) FROM t_user u3 INNER JOIN t_yx_rel_user yu1 ON u3.id = yu1.user_id WHERE yu1.sum_invest_amount > 0 AND ( u3.refferee = u.mobile OR u3.refferee = u.staffId ) ) as refferInvestCount FROM t_user u LEFT JOIN t_yx_rel_user yu ON u.id = yu.user_id
and u.mobile like "%${userMobile}%"
and u.realName like "%${userName}%"
and (u.staffId is null or u.staffId = '')
and u.staffId is not null and u.staffId != ''
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0) < #{rakebackConfigMap.sliveramount1}
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0)+ifnull(yu.sum_b_invite_invest_amount,0) < #{rakebackConfigMap.sliveramount2}
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0) >= #{rakebackConfigMap.sliveramount1}
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0) < #{rakebackConfigMap.goldamount1}
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0)+ifnull(yu.sum_b_invite_invest_amount,0) >= #{rakebackConfigMap.sliveramount2}
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0)+ifnull(yu.sum_b_invite_invest_amount,0) < #{rakebackConfigMap.goldamount2}
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0) >= #{rakebackConfigMap.goldamount1}
and ifnull(yu.sum_invest_amount,0)+ifnull(yu.sum_a_invite_invest_amount,0)+ifnull(yu.sum_b_invite_invest_amount,0) >= #{rakebackConfigMap.goldamount2}
order by yu.sum_rebate_amount desc,u.regDate desc
limit 20000
