mapper.select:
SELECT t1.BORROW_ID AS 'borrowId', tb.id AS 'repaymentId', tb.period AS 'period', t1.borrowCode AS 'borrowCode', t1.loanNumber AS 'loanNumber', tu.realName AS 'name', CONCAT(SUBSTR(tu.mobile,1,3),"****",SUBSTR(tu.mobile,-4)) AS 'mobile', t1.MAXTENDERSUM AS 'borrowAmount', tb.month_capital AS 'monthCapital', tb.month_interest AS 'monthInterest', tb.late_charge_origin AS 'lateChargeOrigin', tb.fails_charge_origin AS 'failsChargeOrigin', tb.advances_amount AS 'advancesAmount', tb.advances_time AS 'advancesTime', tb.offset_amount AS 'offsetAmount', tb.offset_time AS 'offsetTime', tb.repay_time AS 'repaymentDate', tb.overdue_day AS 'overdueDay', tb.is_advances AS 'advanceStatus' FROM t_borrow t1 LEFT JOIN t_user tu ON t1.USERID = tu.id LEFT JOIN t_borrow_billplan tb ON t1.loanNumber = tb.apply_id WHERE t1.BORROWSTATUS = '11' AND tb.repay_status = '1' AND tb.is_overdue = '1'
AND t1.borrowCode = #{borrowCode}
AND t1.loanNumber = #{loanNumber}
AND tu.realName LIKE '%${name}%'
AND tb.overdue_day >= #{overdueDayStart}
AND tb.overdue_day <= #{overdueDayEnd}
ORDER BY t1.borrowCode, tb.period

SELECT t1.BORROW_ID AS 'borrowId', tb.id AS 'repaymentId', tb.period AS 'period', t1.borrowCode AS 'borrowCode', t1.loanNumber AS 'loanNumber', tu.realName AS 'name', CONCAT(SUBSTR(tu.mobile,1,3),"****",SUBSTR(tu.mobile,-4)) AS 'mobile', t1.MAXTENDERSUM AS 'borrowAmount', tb.month_capital AS 'monthCapital', tb.month_interest AS 'monthInterest', tb.late_charge_origin AS 'lateChargeOrigin', tb.fails_charge_origin AS 'failsChargeOrigin', tb.advances_amount AS 'advancesAmount', tb.advances_time AS 'advancesTime', tb.offset_amount AS 'offsetAmount', tb.offset_time AS 'offsetTime', tb.repay_time AS 'repaymentDate', tb.overdue_day AS 'overdueDay', tb.is_advances AS 'advanceStatus' FROM t_borrow t1 LEFT JOIN t_user tu ON t1.USERID = tu.id LEFT JOIN ( SELECT * FROM t_borrow_billplan WHERE apply_id in ( SELECT tmp.apply_id FROM t_borrow_billplan tmp WHERE tmp.repay_status = '1' AND tmp.is_overdue = '1' GROUP BY tmp.apply_id HAVING COUNT(tmp.apply_id) >= 3 ) ) tb ON t1.loanNumber = tb.apply_id WHERE t1.BORROWSTATUS = '11' AND tb.repay_status = '1' AND tb.is_overdue = '1'
AND t1.borrowCode = #{borrowCode}
AND t1.loanNumber = #{loanNumber}
AND tu.realName LIKE '%${name}%'
AND tb.overdue_day >= #{overdueDayStart}
AND tb.overdue_day <= #{overdueDayEnd}
ORDER BY t1.borrowCode, tb.period
