mapper.select:
SELECT 

FROM t_yx_coupon a

WHERE a.id = #{id}

        SELECT 

        FROM t_yx_coupon a 


            a.delflag = '0'

                AND a.coupon_type_id = #{couponTypeId}


			 AND DATE_FORMAT(a.`createdate`, '%Y-%m-%d')   >=   #{startCreateDate}



				AND DATE_FORMAT(a.`createdate`, '%Y-%m-%d')   <=  #{endCreateDate}



                AND a.status = #{status}


                AND a.end_time  <  NOW()





              ORDER BY a.createdate desc ,${page.orderBy}


             ORDER BY a.createdate desc

        SELECT 

        FROM t_yx_coupon a  


            a.delflag = '0' AND a.status IN('1','2','3','4','5')

                AND a.coupon_type_id = #{couponTypeId}


			 AND DATE_FORMAT(a.`createdate`, '%Y-%m-%d')   >=   #{startCreateDate}



				AND DATE_FORMAT(a.`createdate`, '%Y-%m-%d')   <=  #{endCreateDate}


                AND a.status = #{status}


                AND a.end_time  <  NOW()




                ORDER BY a.createdate desc ,${page.orderBy} 


            ORDER BY a.createdate desc

mapper.update:
        UPDATE t_yx_coupon SET 
        updateby = #{updateby},
        status = #{status},
		audit_time =NOW(),
		remarks=#{auditContent}
        WHERE id = #{id}

UPDATE t_yx_coupon SET status = '5'
WHERE id IN

    #{item}
