mapper.select:
SELECT 

FROM t_yx_coupon_group a

WHERE a.coupon_group_id = #{couponGroupId}

        SELECT 

        FROM t_yx_coupon_group a

  	    where  1=1 


				AND a.coupon_group_name like concat('%',#{couponGroupName},'%')


				AND    a.coupon_group_status = #{couponGroupStatus}



				AND DATE_FORMAT(a.`sendTime`, '%Y-%m-%d')  >=   #{startSendDate}



				AND DATE_FORMAT(a.`sendTime`, '%Y-%m-%d')   <=  #{endSendDate}


         ORDER BY a.createdate   desc

SELECT 

FROM t_yx_coupon_group a


    a.coupon_group_status IN('1','2','3')

        AND a.coupon_group_name like concat('%',#{couponGroupName},'%')


        AND    a.coupon_group_status = #{couponGroupStatus}



        AND DATE_FORMAT(a.`sendTime`, '%Y-%m-%d')  >=   #{startSendDate}



        AND DATE_FORMAT(a.`sendTime`, '%Y-%m-%d')   <=  #{endSendDate}


 ORDER BY a.createdate   desc

SELECT mobile AS 'allMobile', CONCAT(SUBSTRING(mobile,1,3),'****',SUBSTRING(mobile,8,4)) AS 'userMobile', COUNT(mobile) AS 'sentCouponSum',createDate, COUNT(CASE WHEN `status`='0' THEN 'wy' ELSE NULL END) AS 'unusedCouponSum', COUNT(CASE WHEN `status`='1' THEN 'sy' ELSE NULL END) AS 'usedCouponSum', COUNT(CASE WHEN `status`='2' THEN 'sx' ELSE NULL END) AS 'expiredCouponSum' FROM t_yx_coupon_log WHERE 1=1
AND coupon_group_id=#{couponGroupId}
AND activity_id=#{activityId}
GROUP BY mobile
LIMIT 0,#{endLimit}

SELECT CONCAT(SUBSTRING(mobile,1,3),'****',SUBSTRING(mobile,8,4)) AS 'userMobile', COUNT(mobile) AS 'sentCouponSum', DATE_FORMAT(createDate, '%Y-%m-%d %H:%i:%s') AS sentdTime, COUNT(CASE WHEN `status`='0' THEN 'wy' ELSE NULL END) AS 'unusedCouponSum', COUNT(CASE WHEN `status`='1' THEN 'sy' ELSE NULL END) AS 'usedCouponSum', COUNT(CASE WHEN `status`='2' THEN 'sx' ELSE NULL END) AS 'expiredCouponSum'
FROM t_yx_coupon_log WHERE 1=1
AND coupon_group_id=#{couponGroupId}
AND activity_id=#{activityId}
GROUP BY mobile
LIMIT 0,#{endLimit}

mapper.update:
UPDATE t_yx_coupon_group SET coupon_group_status='1',
authTime=NOW(),
updateDate=NOW(),
updateBy=#{updateby}
WHERE coupon_group_id=#{couponGroupId}

mapper.select:
SELECT  
FROM t_yx_coupon_relation a INNER JOIN t_yx_coupon b ON a.coupon_id=b.id
    WHERE a.relation_type='group' AND a.main_id=#{couponGroupId}

SELECT b.id AS "id", b.coupon_type_id AS "couponTypeId", b.face_value AS "faceValue", b.effective_days AS "effectiveDays", DATE_FORMAT(b.begin_time,"%Y-%m-%d %T") AS "beginTime", DATE_FORMAT(b.end_time,"%Y-%m-%d %T") AS "endTime", b.loan_amount_min AS "loanAmountMin", b.loan_amount_sum AS "loanAmountSum", b.loan_term_list AS "loanTermList", b.loan_channel_list AS "loanChannelList", b.createdate AS "createdate", DATE_FORMAT(a.use_time,"%Y-%m-%d %T") AS 'useTime' FROM t_yx_coupon_log a INNER JOIN t_yx_coupon b ON a.coupon_id=b.id WHERE a.status='1' AND a.mobile =#{userMobile}
AND a.coupon_group_id=#{couponGroupId}
AND a.activity_id=#{activityId}
